import logo from './logo.svg';
import './App.css';
import React from "react";

class App extends React.Component {

  // Constructor
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false
    };
  }


  // ComponentDidMount is used to
  // execute the cod
  componentDidMount() {
      const url ="https://zccabhishek98.zendesk.com/api/v2/tickets.json?page=1"
    fetch(
        "https://zccabhishek98.zendesk.com/api/v2/tickets.json",{method: 'GET',headers:{Authorization: 'Basic'+'YXhuMjAwMDYyQHV0ZGFsbGFzLmVkdS90b2tlbjpsVjNkcURBaG1NUXRadG4yaHR3a1JWY1g0cXhRdDRCUGlUUEY3aXVR'}})
        .then((res) => res.json())
        .then((json) => {
          this.setState({
            items: json,
            DataisLoaded: true
          });
        })
  }
  render() {
    const { DataisLoaded, items } = this.state;
    if (!DataisLoaded) return <div>
      <h1> Please wait some time.... </h1> </div> ;

    return (
        <div className = "App" style={{textAlign: 'left'}}>
          <h1> Fetch data from an api in react </h1>
            {
            items.map((item) => (
              <ul>
                Id: {item.id},
                User_Name: { item.username },
                Full_Name: { item.name },
                User_Email: { item.email }
              </ul>
          ))
        }
        </div>
    );
  }
}

export default App;
