import logo from './logo.svg';
import zen from './Zen.svg';
import React from "react";
import axios from "axios";
import Spinner from 'react-bootstrap/Spinner';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class App extends React.Component {

  // Constructor
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false
    };
  }


  // ComponentDidMount is used to
  // execute the cod
  componentDidMount() {

      fetch('https://cors-anywhere.herokuapp.com/https://zccabhishek98.zendesk.com/api/v2/requests.json', {mode:'cors' ,headers: {'Authorization': `Basic YXhuMjAwMDYyQHV0ZGFsbGFzLmVkdS90b2tlbjpsVjNkcURBaG1NUXRadG4yaHR3a1JWY1g0cXhRdDRCUGlUUEY3aXVR`}})
          .then(async response => {
              const data = await response.json();

              // check for error response
              if (!response.ok) {
                  // get error message from body or default to response statusText
                  const error = (data && data.message) || response.statusText;
                  return Promise.reject(error);
              }

              this.setState({ items: data.requests , DataisLoaded: true})
          })
          .catch(error => {
              this.setState({ errorMessage: error.toString() });
              console.error('There was an error!', error);
              alert('There was an error!' + error);
          });



      // fetch(
      //   "https://zccabhishek98.zendesk.com/api/v2/requests.json")
      //   .then((res) => res.json())
      //   .then((json) => {
      //     this.setState({
      //       items: json,
      //       DataisLoaded: true
      //     });
      //   })
  }
  render() {
      const { DataisLoaded, items } = this.state;
      if (!DataisLoaded) return (
          <div style={{display: 'flex', justifyContent: 'center'}}>
              <Spinner animation="border">
                  <span className="visually-hidden">Tickets are being fetched</span>
              </Spinner>
          </div>
      )
      return (
          <>
              <Navbar bg="dark" variant="dark">
                  <Container>
                      <Navbar.Brand href="#home">Navbar</Navbar.Brand>
                  </Container>
              </Navbar>
          <ul>
              { items.map((x) =>
                  (
                      <ul>
                          id: {x.id} <br />
                          url: {x.url} <br />
                          status: {x.status} <br />
                          subject: {x.subject} <br />
                          <br />
                      </ul>
                  )
              ) }
          </ul>
         </>
      )
  }
}

export default App;
